$(function () {
	var b = new Date();
	$("#content .countdown").jCountdown({
			timeText: b.getFullYear() + 1 + "/" + (b.getMonth() + 1) + "/" + b.getDate() + " " + b.getHours() + ":" + b.getMinutes() + ":" + b.getSeconds() ,
			timeZone: b,
			style: "flip",
			color: "black",
			width: 0,
			textGroupSpace: 15,
			textSpace: 0,
			reflection: false,
			reflectionOpacity: 10,
			reflectionBlur: 0,
			dayTextNumber: 2,
			displayDay: !0,
			displayHour: !0,
			displayMinute: !0,
			displaySecond: !0,
			displayLabel: false,
			onFinish: function() {}
		})
			
})
